jQuery(function(){
    /**
     * Place the CSRF token as a header on all pages for access in AJAX requests
     */
    jQuery.ajaxSetup({
        headers: {
            'X-CSRF-TOKEN': jQuery('meta[name="_token"]').attr('content')
        }
    });

    /**
     * Add the data-method="delete" forms to all delete links
     */
    addDeleteForms();

    /**
     * Generic confirm form delete using Sweet Alert
     */
    jQuery('body').on('submit', 'form[name=delete_item]', function(e)
    {
        if(!this.classList.contains('list'))
        {
            e.preventDefault();

            var form    = this,
                link    = jQuery(form).parents('a[data-method=delete]'),
                cancel  = (link.attr('data-trans-button-cancel')) ? link.attr('data-trans-button-cancel') : 'Cancel',
                confirm = (link.attr('data-trans-button-confirm')) ? link.attr('data-trans-button-confirm') : 'Yes, delete',
                title   = (link.attr('data-trans-title')) ? link.attr('data-trans-title') : 'Warning',
                text    = (link.attr('data-trans-text')) ? link.attr('data-trans-text') : 'Are you sure you want to delete this item?';

            swal({
                title: title,
                text:  text,
                type: "warning",
                showCancelButton: true,
                cancelButtonText: cancel,
                confirmButtonColor: "#DD6B55",
                confirmButtonText: confirm
            }).then(function() {
                form.submit();
            }).catch(swal.noop);
        }
    });

    /**
     * Generic confirm form delete using Sweet Alert
     */
    jQuery(document).on('click', '[data-list-method]', function(e)
    {
        e.preventDefault();

        if(!e.shiftKey)
        {
            var form    = jQuery(this).find('form'),
                link    = jQuery(form).parents('a[data-method=delete]'),
                elem    = jQuery(form).parents('a[data-list-method=delete]'),
                cancel  = (link.attr('data-trans-button-cancel')) ? link.attr('data-trans-button-cancel') : 'Cancel',
                confirm = (link.attr('data-trans-button-confirm')) ? link.attr('data-trans-button-confirm') : 'Yes, delete',
                title   = (link.attr('data-trans-title')) ? link.attr('data-trans-title') : 'Warning',
                text    = (link.attr('data-trans-text')) ? link.attr('data-trans-text') : 'Are you sure you want to delete this item?';

            swal({
                title: title,
                text:  text,
                type: "warning",
                showCancelButton: true,
                cancelButtonText: cancel,
                confirmButtonColor: "#DD6B55",
                confirmButtonText: confirm
            }).then(function()
            {
                jQuery('a[data-list-method=delete]').css('pointer-events', 'none').attr('disabled', true);
                jQuery('[data-title="Modify"]').css('pointer-events', 'none').attr('disabled', true);

                if(FTXCommon.Utils.browserIs('ie'))
                {
                    var clonedForm = form.clone();
                    clonedForm.appendTo('body');
                    clonedForm.submit();
                }
                else
                {
                    form.submit();
                }
            }).catch(swal.noop);
        }
    });

    /**
     * Bind all bootstrap tooltips
     */
    jQuery("[data-toggle=\"tooltip\"]").tooltip();

    /**
     * Bind all bootstrap popovers
     */
    jQuery("[data-toggle=\"popover\"]").popover();

    /**
     * This closes the popover when its clicked away from
     */
    body.on('click', function (e) {
        jQuery('[data-toggle="popover"]').each(function () {
            if (!jQuery(this).is(e.target) && jQuery(this).has(e.target).length === 0 && jQuery('.popover').has(e.target).length === 0) {
                jQuery(this).popover('hide');
            }
        });
    });
});

