/**
 * Custom Serialization Function (HTMLElement)
 *
 * @param returnType
 * @returns {*}
 */
HTMLElement.prototype.serialize = function(returnType)
{
    var obj         = {},
        usedNames   = [],
        elements    = this.querySelectorAll('input, select, textarea');
        returnType  = returnType == undefined ? 'object' : (_.contains(['json', 'object', 'query'], returnType) ? returnType : 'object');

    for(var i = 0; i < elements.length; ++i)
    {
        var element = elements[i],
            name    = element.name,
            value   = element.value;

        if(_.contains(usedNames, name) || element.disabled)
        {
            continue;
        }

        if(element.nodeName == 'INPUT' && _.contains(['checkbox', 'radio'], element.type))
        {
            var checkboxElements = document.querySelectorAll('[name="'+name+'"]');

            if(checkboxElements.length)
            {
                var valArr = [];

                for(var c = 0; c < checkboxElements.length; c++)
                {
                    if(checkboxElements[c] && checkboxElements[c].checked === true)
                    {
                        valArr.push(checkboxElements[c].value);
                    }
                }

                if(_.isArray(valArr) && typeof valArr[0] !== 'undefined' && valArr.length == 1)
                {
                    if(valArr[0] == 1)
                    {
                        // noinspection JSValidateTypes
                        valArr = 1;
                    }
                    else if(valArr[0] == 0)
                    {
                        // noinspection JSValidateTypes
                        valArr = 0;
                    }
                    else if(typeof valArr[0] === 'string' && valArr[0] != '1' && valArr[0] != '0')
                    {
                        valArr = valArr[0];
                    }
                }

                obj[name] = valArr;
                usedNames.push(name);
                continue;
            }

            element = document.querySelector('[name="'+name+'"]:checked');
            value   = element && element.checked ? 1 : 0;

            obj[name] = value;
            usedNames.push(name);
            continue;
        }

        if(name)
        {
            obj[name] = value;
        }
    }

    if(returnType == 'object')
    {
        return obj;
    }
    else if(returnType == 'query')
    {
        var serializeQuery = function(obj)
        {
            var str = [];

            for(var p in obj)

                if(obj.hasOwnProperty(p))
                {
                    str.push(encodeURIComponent(p) + "=" + encodeURIComponent(obj[p]));
                }

            return str.join("&");
        };

        return serializeQuery(obj);
    }
    else if(returnType == 'json')
    {
        return JSON.stringify(obj);
    }
    else
    {
        return obj;
    }
};

/**
 * Custom Populate Function
 *
 * @param formObj
 */
HTMLElement.prototype.populateForm = function(formObj)
{
    var elements = this.querySelectorAll('input, select, textarea');

    for(var i = 0; i < elements.length; ++i)
    {
        var element = elements[i],
            name    = element.name;

        if(element.disabled)
        {
            continue;
        }

        var formValue = formObj[name] != undefined ? formObj[name] : false;

        if(formValue != undefined || formValue != false)
        {
            if(element.nodeName == 'SELECT')
            {
                element.value = formValue;
            }
            else if(_.contains(['text', 'textarea', 'number'], element.type))
            {
                element.value = formValue;
            }
            else if(element.type == 'radio')
            {
                element.checked = element.value == formValue;
            }
            else if(element.type == 'checkbox')
            {
                //element.checked = Boolean(element.value == formValue);
                element.checked = Boolean(formValue);
            }

            FTXCommon.Utils.fireCustomEvent(element, 'change');
        }
    }
};

