/**
 *
 * Try Get
 *
 * Returns a result given a path in any nested structure using Dot Notation. If the result is not found or isset(result) is false, returns defaultResult.
 *
 * @param {object|array} variable
 * @param {string|array} pathToGet
 * @param {*} defaultResult
 * @param {function} extraConditionalCallback
 * @returns {*}
 */
function try_get(variable, pathToGet, defaultResult, extraConditionalCallback)
{
    pathToGet                = toString(pathToGet);
    defaultResult            = defaulted(defaultResult, null);
    extraConditionalCallback = defaulted(extraConditionalCallback, function(val){return isset(val);}, is_function);

    if(!isset(pathToGet))
    {
        return defaultResult;
    }

    if(!isset(defaultResult))
    {
        defaultResult = null;
    }

    var validVariable = isset(variable),
        containsDot   = false;

    if(is_string(pathToGet))
    {
        containsDot = pathToGet.indexOf('.') !== -1;
    }

    if(validVariable && is_string(pathToGet) && !containsDot)
    {
        return isset(variable[pathToGet]) && extraConditionalCallback(variable[pathToGet]) ? variable[pathToGet] : defaultResult;
    }
    else if(validVariable && isset(pathToGet))
    {
        if(is_string(pathToGet) && containsDot)
        {
            pathToGet = pathToGet.split('.');
        }

        if(is_array(pathToGet))
        {
            var result = variable;

            _.every(pathToGet, function(key)
            {
                if(is_string(key) && isset(result[key]))
                {
                    result = result[key];
                    return true;
                }

                result = defaultResult;
                return false;
            });
        }

        return extraConditionalCallback(result) ? result : defaultResult;
    }

    return defaultResult;
}

/**
 *
 * Try Set
 *
 * Attempts to set the value at the given path, returns true if successful, otherwise returns false
 *
 * @param variable
 * @param pathToSet
 * @param valueToSet
 * @returns {boolean}
 */
