/**
 * Underscore To Words
 *
 * Takes an underscore_separated_string and makes it into Formal Words.
 *
 * @param {string} string
 * @returns {string}
 */
function underscoreToWords(string)
{
    if(!is_string(string) || string.length <= 0)
    {
        return '';
    }

    var strings = string.split('_');

    strings = _.map(strings, function(string)
    {
        return string.charAt(0).toUpperCase() + string.slice(1);
    });

    return strings.join(' ');

}

/**
 * Underscore To Words
 *
 * Takes Formal Words and makes it into an underscore_separated_string.
 *
 * @param {string} string
 * @returns {string}
 */
