function objectValues(obj)
{
    if(is_function(try_get(Object, 'values')))
    {
        return Object.values(obj);
    }

    var res = [];

    for(var i in obj)
    {
        if(obj.hasOwnProperty(i))
        {
            res.push(obj[i]);
        }
    }

    return res;
}


