/**
 * Tooltip List
 * Creates a tooltip for the element given a list of values or objects
 *
 * @param {Element} element
 * @param {Array} list
 * @returns {Element}
 */
function tooltipList(element, list)
{
    if(!empty(list) && is_array(list))
    {
        var first = _.first(list);

        if(is_object(first))
        {
            list = _.map(list, function(item)
            {
                return objectValues(item).join(' - ');
            });
        }

        element.title = list.join('<br>');

        $(element).tooltip({
            html      : true,
            container : 'body',
            placement : 'right',
        });
    }

    return element;
}

/**
 * Popup Table
 * Onclick will open a modal containing the items in a table
 *
 * @param {Element} element
 * @param {Array} items
 * @param {string} title
 * @param {Array|null} columns
 * @returns {Element}
 */
function popupTable(element, items, title, columns)
{
    title = defaulted(title, element.innerText);

    if(!empty(items) && is_array(items))
    {
        var first = _.first(items);

        if(!is_object(first))
        {
            items = _.map(items, function(item)
            {
                var obj = {};

                obj[wordsToUnderscore(title)] = item;

                return obj;
            });

            first = _.first(items);
        }

        if(!is_array(columns))
        {
            columns = _.map(first, function(value, key)
            {
                return {
                    key    : key,
                    header : underscoreToWords(key),
                };
            });
        }

        element.style.cursor         = 'pointer';
        element.style.color          = 'blue';
        element.style.textDecoration = 'underline';
        element.onclick              = function()
        {
            var modalBox = new ModalBox({
                modalClass          : 'modal-dialog',
                modalTitle          : title,
                modalTitleClass     : 'modal-title',
                modalFooter     : {
                    show        : true,
                    close       : true,
                    closeText   : 'Close',
                },
            });

            modalBox.setBodyContents((new DomUtils()).createTable(columns, items, {
                classList: [
                    'table-bordered',
                    'table-striped',
                    'table-condensed',
                    'w100p',
                ],
            }, {
                withoutHeader: _.size(columns) <= 1
            }));

            modalBox.open();
        };
    }

    return element;
}


/**
 * Make Url from params and append to original url
 *
 * @param {String} url
 * @param {Object} params
 * @return {string}
 */
function makeUrlFromParams(url, params)
{
    var out = [];

    for(var key in params)
    {
        if(params.hasOwnProperty(key))
        {
            out.push(key + '=' + encodeURIComponent(params[key]));
        }
    }

    return url + (url.search(/\?/g) !== -1 ? '&' : '?') + out.join('&');
}

function fixDataTableSearch()
{
    $('div.dataTables_filter input').off('keyup.DT input.DT');

    var searchDelay = null;

    $('div.dataTables_filter input').on('keyup', function() {
        var search       = $('div.dataTables_filter input').val(),
            tableWrapper = $(this).closest('.dataTables_wrapper'),
            table        = tableWrapper.find('table'),
            dataTable    = table.DataTable(),
            timeout      = try_get(dataTable.settings()[0], 'searchDelay', 2000);

        clearTimeout(searchDelay);

        searchDelay = setTimeout(function()
        {
            if(search != null)
            {
                dataTable.search(search).draw();
            }
        }, timeout);
    });
}

