function addHotkeyHandler()
{
    $(function()
    {
        var hotkeyElements = document.querySelectorAll('[data-hotkey]');

        for(var i = 0, hotkeyElement; (hotkeyElement = hotkeyElements[i]) && isElement(hotkeyElement); i++)
        {
            var data = (function()
                {
                    var data = hotkeyElement.dataset.hotkey.toLowerCase();

                    try
                    {
                        data = JSON.parse(data);
                    }
                    catch(e){}

                    if(is_string(data))
                    {
                        data = [data];
                    }

                    return data;
                })(),
                signature = data[0],
                action    = data[1] || 'click';

            if(is_function(hotkeyElement[action]))
            {
                keyboardJS.bind(signature, (function(hotkeyElement, action, e)
                {
                    hotkeyElement[action]();
                    e.preventDefault();
                    e.stopPropagation();
                }).bind(null, hotkeyElement, action));
            }
        }

        bindSaveHotkey();
    });
}

function bindSaveHotkey()
{
    var saveButton = document.querySelector('[data-save]');

    if(saveButton)
    {
        keyboardJS.bind('ctrl > s', function(e)
        {
            saveButton.click();
            e.preventDefault();
            e.stopPropagation();
        });
    }
}

